import React, { useState, useEffect, useCallback } from "react";
import "./App.css";

function App() {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isVideoLoading, setIsVideoLoading] = useState(false);
  const [videoUrl, setVideoUrl] = useState(null);
  const [error, setError] = useState(null);

  // Fetch video list when the component mounts
  useEffect(() => {
    fetchVideos();
  }, []);

  // Function to fetch all video data from the server
  const fetchVideos = async () => {
    setIsLoading(true);
    setError(null);  // Reset error before fetching
    try {
      const response = await fetch("http://127.0.0.1:5000/api/videos");
      if (!response.ok) throw new Error("Failed to fetch video list.");
      const data = await response.json();
      setVideos(data);
    } catch (error) {
      console.error("Error fetching videos:", error);
      setError("There was an issue loading the videos. Please try again later.");
    } finally {
      setIsLoading(false);
    }
  };

  // Memoize handleVideoClick to avoid unnecessary re-renders
  const handleVideoClick = useCallback(async (filename) => {
    setSelectedVideo(filename);
    setIsVideoLoading(true);
    setError(null);  // Reset error before fetching
    const videoStreamUrl = await fetchVideoStream(filename);
    if (videoStreamUrl) {
      setVideoUrl(videoStreamUrl);
    } else {
      setError("Failed to fetch the video stream. Please try again.");
    }
    setIsVideoLoading(false);
  }, []);

  // Function to fetch video stream URL
  const fetchVideoStream = async (filename) => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/api/video/${filename}`);
      if (!response.ok) throw new Error("Failed to fetch video stream.");
      const data = await response.json();
      if (data.presigned_url) {
        return data.presigned_url;
      } else {
        throw new Error("No presigned URL found.");
      }
    } catch (error) {
      console.error("Error fetching video stream:", error);
      setError("Error fetching the video stream.");
      return null;
    }
  };

  return (
    <div className="App">
      <h1>Video List</h1>

      {/* Show loading state for videos */}
      {isLoading ? (
        <p>Loading videos...</p>
      ) : error ? (
        <p>{error}</p>
      ) : videos.length > 0 ? (
        <div>
          <ul>
            {videos.map((video, index) => (
              <li key={index}>
                <button onClick={() => handleVideoClick(video.filename)}>
                  {video.title}
                </button>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>No videos available. Please try again later.</p>
      )}

      {/* Display selected video player */}
      {selectedVideo && videoUrl && (
        <div className="video-player">
          <h2>Now Playing: {selectedVideo}</h2>
          {isVideoLoading ? (
            <p>Loading video...</p>
          ) : (
            <video controls width="600" height="400" src={videoUrl}>
              Your browser does not support the video tag.
            </video>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
