import React, { useState, useEffect } from 'react';

const VideoPlayer = ({ videoFilename }) => {
  const [videoUrl, setVideoUrl] = useState(null);

  useEffect(() => {
    // Fetch the pre-signed URL from the backend
    fetch(`http://127.0.0.1:5000/api/video-url/${videoFilename}`)
      .then(response => response.json())
      .then(data => {
        if (data.url) {
          setVideoUrl(data.url);
        }
      })
      .catch(err => console.error('Error fetching video URL:', err));
  }, [videoFilename]);

  return (
    <div>
      {videoUrl ? (
        <video width="100%" height="auto" controls>
          <source src={videoUrl} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      ) : (
        <p>Loading video...</p>
      )}
    </div>
  );
};

const VideoList = () => {
  const [videos, setVideos] = useState([]);

  useEffect(() => {
    // Fetch the video list from the backend
    fetch('http://127.0.0.1:5000/api/videos')
      .then(response => response.json())
      .then(data => setVideos(data))
      .catch(err => console.error('Error fetching video list:', err));
  }, []);

  return (
    <div>
      <h1>Video List</h1>
      <div>
        {videos.map((video, index) => (
          <div key={index} className="video-item">
            <h2>{video.title}</h2>
            <p>{video.description}</p>
            <p>Size: {video.size}</p>
            <p>Duration: {video.duration}</p>
            <VideoPlayer videoFilename={video.filename} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default VideoList;
