import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [videoUrl, setVideoUrl] = useState(null); // To store the URL for video stream

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    setIsLoading(true);
    try {
      const response = await fetch("http://127.0.0.1:5000/api/videos");
      const data = await response.json();
      setVideos(data);
      setIsLoading(false);
    } catch (error) {
      console.error("Error fetching videos:", error);
      setIsLoading(false);
    }
  };

  const handleVideoClick = async (filename) => {
    setSelectedVideo(filename);
    const videoStreamUrl = await fetchVideoStream(filename);
    if (videoStreamUrl) {
      setVideoUrl(videoStreamUrl); // Set the presigned URL for video playback
    } else {
      alert("Failed to fetch the video.");
    }
  };

  const fetchVideoStream = async (filename) => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/api/video/${filename}`);
      const data = await response.json();
      if (data.presigned_url) {
        return data.presigned_url; // Use the presigned URL to stream video
      } else {
        throw new Error("No presigned URL found.");
      }
    } catch (error) {
      console.error("Error fetching video stream:", error);
      return null;
    }
  };

  return (
    <div className="App">
      <h1>Video List</h1>
      {isLoading ? (
        <p>Loading videos...</p>
      ) : (
        <div>
          <ul>
            {videos.map((video, index) => (
              <li key={index}>
                <button onClick={() => handleVideoClick(video.filename)}>
                  {video.title}
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}

      {selectedVideo && videoUrl && (
        <div className="video-player">
          <h2>{selectedVideo}</h2>
          <video controls width="600" height="400" src={videoUrl}>
            Your browser does not support the video tag.
          </video>
        </div>
      )}
    </div>
  );
}

export default App;
