import React, { useState, useEffect } from "react";
import axios from "axios";
import ReactPlayer from "react-player";

const App = () => {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);

  useEffect(() => {
    axios.get("http://127.0.0.1:5000/api/videos").then((response) => {
      setVideos(response.data);
    });
  }, []);

  const playVideo = (filename) => {
    setSelectedVideo(`http://127.0.0.1:5000/api/video/${filename}`);
  };

  return (
    <div>
      <h1>Video Streaming Platform</h1>
      <div style={{ display: "flex", gap: "20px" }}>
        {videos.map((video) => (
          <div key={video.title}>
            <h3>{video.title}</h3>
            <p>{video.description}</p>
            <button onClick={() => playVideo(video.filename)}>Play</button>
          </div>
        ))}
      </div>
      {selectedVideo && (
        <div>
          <ReactPlayer url={selectedVideo} controls />
        </div>
      )}
    </div>
  );
};

export default App;
