import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    setIsLoading(true);
    try {
      const response = await fetch("http://127.0.0.1:5000/api/videos");
      const data = await response.json();
      setVideos(data);
      setIsLoading(false);
    } catch (error) {
      console.error("Error fetching videos:", error);
      setIsLoading(false);
    }
  };

  const handleVideoClick = (filename) => {
    setSelectedVideo(filename);
  };

  return (
    <div className="App">
      <h1>Video List</h1>
      {isLoading ? (
        <p>Loading videos...</p>
      ) : (
        <div>
          <ul>
            {videos.map((video, index) => (
              <li key={index}>
                <button onClick={() => handleVideoClick(video.filename)}>
                  {video.title}
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}

      {selectedVideo && (
        <div className="video-player">
          <h2>{selectedVideo}</h2>
          <video
            controls
            width="600"
            height="400"
            src={`http://127.0.0.1:5000/api/video/${selectedVideo}`}
          >
            Your browser does not support the video tag.
          </video>
        </div>
      )}
    </div>
  );
}

export default App;
